mapper.insert:

	SELECT IFNULL(MAX(IDX),0) FROM emp;

INSERT INTO emp
	VALUES (
		#{IDX} + 1
		,CONCAT(#{EMP_NUM})
		,#{EMP_NAME}
		,#{DEPT_CD}
		,#{GRADE_CD}
		,#{EMP_TEL}
		,#{EMP_DTEL}
		,#{EMP_EMAIL}
		,#{EMP_PASSWD}
		,#{EMP_POST_NO}
		,#{EMP_ADDR}
		,#{HIRE_DATE}
		,#{WORK_CD}
		,#{PRIV_CD}
		,#{PHOTO}
	)

mapper.select:
SELECT EMP_PASSWD
	FROM emp
		WHERE EMP_EMAIL = #{EMP_EMAIL}

SELECT EMP_NAME, PRIV_CD, IDX
	FROM emp
		WHERE EMP_EMAIL = #{EMP_EMAIL}

SELECT idx
	FROM emp
		WHERE EMP_EMAIL = #{check_email}

SELECT PRIV_CD
	FROM emp
	WHERE EMP_NAME=#{EMP_NAME}

