mapper.select:
SELECT COUNT(*)
	FROM product
	WHERE product_cd = #{product_cd}

mapper.update:
UPDATE product
	SET 


		product_name = #{product.product_name}
		, product_group_bottom_cd = #{product.product_group_bottom_cd}
		, size_des = #{product.size_des}
		, unit = #{product.unit}
		, in_unit_price = #{product.in_unit_price}
		, out_unit_price = #{product.out_unit_price}
		, product_type_cd = #{product.product_type_cd}

		, business_no = #{product.business_no}


		, product_image = #{product.product_image}

		, remarks = #{product.remarks}
	WHERE 
		product_cd = #{product.product_cd}

UPDATE product
	SET product_image = REPLACE(product_image, #{product_image}, "")
	WHERE product_cd = #{product_cd}
