mapper.update:
UPDATE stock
	SET STOCK_QTY = #{update_qty}
		WHERE STOCK_CD = #{stock_cd}

UPDATE stock SET STOCK_QTY = CASE 
	WHEN stock_cd = #{current_stock_cd} THEN STOCK_QTY - #{move_stock_num}
	WHEN stock_cd = #{move_stock_cd} THEN STOCK_QTY + #{move_stock_num}
	END

mapper.insert:
INSERT INTO stock_history(STOCK_CD, CONTROL_TYPE_CD, PRODUCT_CD, QTY, EMP_NUM, STOCK_DATE)
	VALUES (
	#{stock_cd}
	,2
	,#{product_cd}
	,#{update_qty}
	,#{sId}
	,now()
	)

INSERT INTO stock_history(STOCK_CD, CONTROL_TYPE_CD, PRODUCT_CD, SOURCE_STOCK_CD, TARGET_STOCK_CD, QTY, EMP_NUM, STOCK_DATE)
	VALUES (
	#{current_stock_cd}
	,3
	,#{product_cd}
	,#{current_stock_cd}
	,#{move_stock_cd}
	,#{move_stock_num}
	,#{sId}
	,now()
	)
